import React, { Component } from "react";

class Book extends Component {

  state = {
    isEditable: false
  };

  changkeyHandler = (event) => {
    if(event.key=='Enter'){
      this.setState({
        isEditable: false
      })
    }
   }

  render() {

    

    let output = this.state.isEditable ? (
      <input 
        onChange={ e => 
          this.props.changeHandler(e.target.value,this.props.book.id) 
        } 
        onKeyPress={ this.changkeyHandler }
        type="text" 
        placeholder="Enter Name" 
        value={this.props.book.name}
       />
      ) : (
          <p> {this.props.book.name} </p>
      );

    return (
          <li className="list-group-item d-flex">
            {output}
            <span className="ml-auto"> ${this.props.book.price} </span>
            <div>
                <span onClick={ () => this.setState({isEditable :true}) } style={{  margin:20 ,cursor:"pointer" }}>
                  <i className="fas fa-edit"></i>
                </span>
                <span onClick={ () => this.props.deleteHandler(this.props.book.id) } style={{  margin:20 ,cursor:"pointer" }}>
                    <i className="fas fa-trash"></i> 
                </span>
            </div>
          </li>
    )
  }
}

export default Book;
