class App extends Component {

  state = {
    posts : []
  }

  componentDidMount(){
    axios.get('http://countriesnode.herokuapp.com/v1/countries/')
    .then(Response => {
      this.setState({
        posts: Response.data
      })
    })
    .catch(error => console.log(error ))
  }
  render(){

      let {posts} = this.state

      if (posts.length==0){
        return <h1 style={{textAlign: 'center' }}>Looding...</h1>
      }else{
        return (
          <div className="container">
            <ul className="list-group">
              { posts.map(post => 
                <li key ={ post.id } 
                className="list-group-item"> 
                Country : {post.name} <br/> 
                Native : {post.native} <br/>
                Phone : {post.phone} <br/>
                Continent : {post.continent} <br/>
                Capital : {post.capital} <br/>
                Currency : {post.currency} <br/>
                Languages : {post.languages} <br/>
                Native : {post.native} <br/>
                Native : {post.native} <br/>
                Native : {post.native} <br/>
                </li> ) }
            </ul>
          </div>
        )
      }
  }
}

export default App;
