class App extends Component {

  state = {
    books: [
      {
        name: "cooked book",
        price: 190,
        id: 1
      },
      {
        name: "Works book",
        price: 340,
        id: 2
      },
      {
        name: "poem book",
        price: 640,
        id: 3
      },
      {
        name: "Leadership book",
        price: 240,
        id: 4
      }

    ]
  }

  deleteHandler = (id) => {
    let newBooks= this.state.books.filter(book=> book.id != id)
    this.setState({
      books: newBooks
    })
  }

  changeHandler = (name,id) => {
    let newBooks= this.state.books.map(book => {
      if(id=book.id ) {
        return {
          ...book,
             name
        }
      }

      return book
    })

    this.setState({
      books: newBooks
    })
  }
  render() {
    return (
      <div className='App'>
        <Books 
           changeHandler={this.changeHandler.bind(this)} deleteHandler={ this.deleteHandler.bind(this) } books={this.state.books} />
      </div>
    );
  }
}

export default App;
